import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text.toUpperCase();
    setText(newtext);
  };
  const handlelowClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text.toLowerCase();
    setText(newtext);
    console.log(textBold);
  };
  const handleboldClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text;
    setText(newtext);
    if(textBold === "normal"){
    setTextBold("bold");
    }else{
      setTextBold("normal");
    }
    console.log(textBold);
  };
  const handleitalicClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text;
    setText(newtext);
    if(textItalic === "normal"){
    setTextItalic("italic");
    }else{
      setTextItalic("normal");
    }
    console.log(textBold);
  };
  const handleOnChange = (event) => {
    // console.log("text changed");
    setText(event.target.value);
  };
  const handleCopy = () =>{
    let text = document.getElementById("myBox")
    text.select();
    navigator.clipboard.writeText(text.value)
  }
  const handleExtraSpace = () => {
    let text_opt = text.split(/[ ]+/)
    setText(text_opt.join(" "))
  }
  const [text, setText] = useState("Set Text Here2");
  const[textBold,setTextBold] = useState("normal");
  const[textItalic,setTextItalic] = useState("normal");
  return (
    <>
      <div className="container" style={{color:props.mode==="light"?"black":"white"}}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
            style={{fontWeight:textBold, fontStyle:textItalic, backgroundColor:props.mode==="dark"?"grey":"white",color:props.mode==="dark"?"white":"black"}}
          ></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className="btn btn-success mx-3" onClick={handlelowClick}>
          Convert to Lowercase
        </button>
        <button className="btn1 btn-primary mx-2" value={textBold} onClick={handleboldClick} style={{fontWeight:textBold}} >
          B
        </button>
        <button className="btn2 btn-primary mx-2" value={textBold} onClick={handleitalicClick}  style={{fontStyle:textItalic}} >
          I
        </button>
        <button className="btn btn-primary mx-2" value={textBold} onClick={handleCopy}   >
          CopyText
        </button>
        <button className="btn btn-primary mx-2" value={textBold} onClick={handleExtraSpace}   >
          Remove Extra Space
        </button>
      </div>
      <div className="container my-3">
        <h2>Your Text Summary</h2>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.008 * text.split(" ").length} Minutes read</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
