import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text.toUpperCase();
    setText(newtext);
  };
  const handlelowClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text.toLowerCase();
    setText(newtext);
  };
  const handleboldClick = () => {
    //  console.log("btn clicked "+text);
    let newtext = text;
    setText(newtext);
    setTextBold("bold");
    console.log(textBold);
  };
  const handleOnChange = (event) => {
    // console.log("text changed");
    setText(event.target.value);
  };
  const [text, setText] = useState("Set Text Here2");
  const[textBold,setTextBold] = useState("none");
  return (
    <>
      <div className="container">
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="8"
            style={{fontWeight:bold}}
          ></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-3" onClick={handlelowClick}>
          Convert to Lowercase
        </button>
        <button className="btn btn-primary mx-3" value={textBold} onClick={handleboldClick}>
          B
        </button>
      </div>
      <div className="container my-3">
        <h2>Your Text Summary</h2>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.008 * text.split(" ").length} Minutes read</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
